import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import "./css/navigation.css";
import { Avatar } from 'antd';

const navLinks = [
    {
        title: "Home",
        path: '/home'
    },
    {
        title: 'Blog',
        path: '/blog'
    },
    {
        title: 'Contact Us',
        path: '/contact-us'
    }
];

function Navigation() {
    const [sidebarOpen, setSidebarOpen] = useState(false);

    const toggleSidebar = () => {
        setSidebarOpen(!sidebarOpen);
    };

    return (
        <div className='navigation-bar'>
            <div className="container">
                <nav className="navbar navbar-expand-lg navbar-light py-3">
                    <div className="sidebar-header">
                        <a className="navbar-brand" href="/">MiniBlog</a>
                    </div>
                    <button className={`navbar-toggler ${sidebarOpen ? 'active' : ''}`} type="button" onClick={toggleSidebar}>
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className={`collapse navbar-collapse ${sidebarOpen ? 'show' : ''}`}>
                        <div className="navbar-nav">
                            {
                                navLinks.map((link, index) => {
                                    return <a key={index} className="nav-item nav-link" href={link.path}>{link.title}</a>
                                })
                            }
                        </div>

                        <div className='buttons navbar-nav d-lg-flex d-none d-lg-block align-items-center'>
                            <a className="button nav-item nav-link me-2"> <button type="button" className="btn btn-outline-dark">Login</button> </a>
                            <a className="button nav-item nav-link">  <button type="button" className="btn btn-dark">Sign Up</button> </a>
                        </div>
                    </div>
                    {/* <Avatar src='https://gw.alipayobjects.com/zos/rmsportal/KDpgvguMpGfqaHPjicRK.svg' /> */}
                </nav>
            </div>
        </div>
    )
}

export default Navigation;
