import React, { useState, useEffect, useRef } from "react";
import "bootstrap/dist/css/bootstrap.css";
import "./css/user-post.css";
import { Navigate } from "react-router";

export default function UserPost({ post, isFirst }) {
  const [expanded, setExpanded] = useState(false);
  const postTextRef = useRef(null);

  
  const postPageRender = () => {
    return <Navigate to={`/post/${post._id}`} />;
  };
 
  const handleResize = () => {
    if (postTextRef.current?.offsetHeight > 340) {
      setExpanded(true);
    } else {
      setExpanded(false);
    }
    console.log(postTextRef.current?.clientHeight);
    // window.location.reload();
  };

  useEffect(() => {
    handleResize();
    // window.addEventListener('resize', handleResize);

    // return () => {
    //   window.removeEventListener('resize', handleResize);
    // }
  }, [window.innerWidth]);
  
  let renderSummary= <p>{post.summary}</p>
  useEffect(()=>{
    renderSummary= expanded? 
    <p>
    {post.summary.slice(0, 100)}{"..."}
  </p> :
  <p>{post.summary}</p>;
    // renderSummary = {
    //   if (!expanded) {
    //     return <p>{post.summary}</p>;
    //   } else {
    //     return (
    //       <p>
    //         {post.summary.slice(0, 100)}{"..."}
    //       </p>
    //     );
    //   }
    // };
  }, [expanded]);
  

  return (
    <div className="post-container">
      <div onClick={postPageRender} className={`post-image ${isFirst && "first-post"}`}>
        <img src={`${require(`../../rest/uploads/${post.image.slice(8)}`)}`} />
      </div>
      <div
        className={` post-text ${isFirst && "first-post"}`}
        ref={postTextRef}
      >
        <div>
          <h2 onClick={postPageRender}>{post.title}</h2>
          <span>{post.author.username}</span>
          <span>{post.date}</span>
        </div>
        <p> {renderSummary}</p>
      </div>
    </div>
  );
}
