import React, { useEffect, useState } from "react";
import UserPost from "../components/user-post";
// import TagRow from "../components/tag-row";
import "bootstrap/dist/css/bootstrap.css";
import "./css/search.css";

function Search() {
  const [search, setSearch] = useState("");
  const [posts, setPosts]= useState([]);
  const [isRender, setIsRender]= useState(false);
  // const [tags, setTags] = useState([]);
  // useEffect(() => {
  //     fetch(`http://127.0.0.1:8000/tags`).then(async (response) => {
  //       await response.json().then((tags) => {
  //          setTags(tags);
  //       });
  //     }).catch(err=> console.log(err));
  //   }, []);
  useEffect(() => {
    setIsRender(false); // Reset isRender to false when the component mounts
  }, []);
  async function searchPost(ev) {
    ev.preventDefault();
    try {
      await fetch(`http://127.0.0.1:8000/posts?search=${search}`)
      .then(async (res)=>{
        await res.json().then(posts=>{
          setPosts(posts);
          setIsRender(true);
        })
      });
     
    } catch (error) {
      console.error('Error:', error);
    }
  }
  

  return (
    <>
    <div className="search-container input-group rounded">
      <div className="search-component ">
        <form className=" d-flex" onSubmit={searchPost}>
          <input
            type="search"
            name="search"
            className="form-control rounded "
            placeholder="Search"
            value={search}
            onChange={(event) => setSearch(event.target.value)}
            aria-label="Search"
            aria-describedby="search-addon"
          />
          <span className="input-group-text border-0" id="search-addon">
            <button className="btn btn-dark">
              <i className="fas fa-search"></i>
            </button>
          </span>
        </form>
      </div>
      {/* <div className="tags-row-container">
      <TagRow tags={tags} />
      </div> */}
    </div>
    <div className={`${(!isRender)|| isRender && posts.length>0 && 'd-none'}`}>
       <h2>No results found</h2>
    </div>
    <div className={`${isRender && posts.length===0 && "d-none"} search-posts`}>
      {posts.map((post,index)=>{
        return <UserPost post={post} key={index} />
      })}
    </div>
    </>
  );
}

export default Search;
