import React, {useState, useEffect, useRef} from "react";
import "bootstrap/dist/css/bootstrap.css";
import "./css/user-post.css";


export default function UserPost({ post, isFirst }) {
    const [expanded, setExpanded]=useState(false);
    const postTextRef = useRef(null);
    useEffect(() => {
        const handleResize = () => {
          if (postTextRef.current.offsetHeight > 340) {
            setExpanded(true);
          } else {
            setExpanded(false);
          }
        };
    
        handleResize();
    
        window.addEventListener("resize", handleResize);
    
        return () => {
          window.removeEventListener("resize", handleResize);
        };
      }, []);
    
      const toggleExpanded = () => {
        setExpanded(!expanded);
      };
    
      const renderSummary = () => {
        if (expanded) {
          return <p>{post.summary}</p>;
        } else {
          return (
            <p>
              {post.summary.slice(0, 100)}{" "}
              <span className="read-more" onClick={toggleExpanded}>
                Read More
              </span>
            </p>
          );
        }
      };
    
  return (
    <div className="post-container">
        <div className={`post-image ${isFirst && 'first-post'}`}>
      <img src={`${require(`../../rest/uploads/${post.image.slice(8)}`)}`} />
      </div>
        <div className={` post-text ${isFirst && 'first-post'}`} ref={postTextRef} >
            <div>
          <h2>{post.title}</h2>
          <span>{post.author.username}</span>
          <span>{post.date}</span>
          </div>
          <p> {renderSummary()}</p>
        </div>
     
    </div>
  );
}
