import React, { useState, useLayoutEffect, useRef } from "react";
import "bootstrap/dist/css/bootstrap.css";
import "./css/user-post.css";
import { Navigate } from "react-router";

export default function UserPost({ post, isFirst }) {
  const [expanded, setExpanded] = useState(false);
  const postTextRef = useRef(null);

  
  const postPageRender = () => {
    return <Navigate to={`/post/${post._id}`} />;
  };

  useLayoutEffect(() => {
    const handleResize = () => {
      if (postTextRef.current && postTextRef.current.clientHeight > 340) {
        setExpanded(true);
      } else {
        setExpanded(false);
      }
    };

    const handleDelayedResize = setTimeout(handleResize, 100); // Delay the resize handling

    return () => {
      clearTimeout(handleDelayedResize); // Clear the timeout on cleanup
    };
  }, []);
  const renderSummary = () => {
    if (!expanded) {
      return <p>{post.summary}</p>;
    } else {
      return (
        <p>
          {post.summary.slice(0, 100)}{"..."}
        </p>
      );
    }
  };

  return (
    <div className="post-container">
      <div onClick={postPageRender} className={`post-image ${isFirst && "first-post"}`}>
        <img src={`${require(`../../rest/uploads/${post.image.slice(8)}`)}`} />
      </div>
      <div
        className={` post-text ${isFirst && "first-post"}`}
        ref={postTextRef}
      >
        <div>
          <h2 onClick={postPageRender}>{post.title}</h2>
          <span>{post.author.username}</span>
          <span>{post.date}</span>
        </div>
        <p className="truncate"> {renderSummary()}</p>
      </div>
    </div>
  );
}
