import React, { useState, useMemo, useEffect, useRef } from "react";
import { Pagination } from "antd";
import TagRow from "./tag-row";
// import "bootstrap/dist/css/bootstrap.css";
import './css/post-grid.css'

export default function PostGrid({posts,ref}) {
  const [current, setCurrent] = useState(1);
  const [pageSize, setPageSize] = useState(9);
  const stopScroll = useRef(null);

  const onChange = (page) => {
    setCurrent(page);
  };

  const paginatedPosts = useMemo(() => {
    const lastIndex = current * pageSize;
    const firstIndex = lastIndex - pageSize;

    return posts.slice(firstIndex, lastIndex);
  }, [current, pageSize]);
  
  useEffect(() => {
    stopScroll.current?.scrollIntoView({ behavior: 'smooth' });
  }, [current,pageSize]);


  return (
    <section className="pagination-container">
      <section className="post-grid container">
        {paginatedPosts.map((post, index) => {
         return   <div className="post-container">
            <figure>
              <a href={post.link}>
                <img 
                  src={require(`../assets/images/${post.image}`)}
                  alt={post.image}
                />
              </a>
            </figure>
            <TagRow tags={post.categories} />
            <h2>{post.title}</h2>
            <p className="author-text">
              <span>
                By:
                <a href={`/authors/${post.author}`}>{post.author}</a>
              </span>
              <span>- {post.date}</span>
            </p>
            <p className="description-text">{post.description}</p>
            <a href={post.link}>Read More...</a>
          </div>;
        })}
      </section>
      <Pagination
        defaultCurrent={current}
        showSizeChanger
        onShowSizeChange={setPageSize}
        showQuickJumper
        onShowQuickJumper={onChange}
        onChange={onChange}
        total={posts.length}
      />
    </section>
  );
}
