import React from "react";
import "bootstrap/dist/css/bootstrap.css";
import "./css/user-post.css";
import { useNavigate } from "react-router";


export default function UserPost({ post, isFirst }) {
  const navigate= useNavigate();

  const postPageRender = () => {

    navigate(`/post/${post._id}`);
  };


  return (
    <>
    <h1 style={{'margin-top':'78px'}}>Posts by {`${post.author.username}`}</h1>
    <div className="post-box">
      <div
        onClick={postPageRender}
        className={`post-image ${isFirst && "first-post"}`}
      >
        <img
          src={`${require(`../../rest/uploads/${post.image.slice(8)}`)}`}
          alt="Post Image"
        />
      </div>
      <div className={`post-text ${isFirst && "first-post"}`}>
        <div>
          <h2 onClick={postPageRender}>{post.title}</h2>
          <span>{post.author.username}</span>
          <span>{post.date}</span>
        </div>
        <p>{post.summary}</p>
      </div>
    </div>
    </>
  );
}
