import Recat, { useEffect } from 'react';
import categoryColors from "./styles.js";
import './css/tag-row.css'

export default function TagRow({tags}){

  useEffect(()=>{
   fetch()
  }, []);

  return (
    <div className="tags-container">
    {tags.map((tag, index) => {
      return (
        <span
          key={index}
          className="tag"
          style={{ backgroundColor: tag.color }}
        >
          {tag.toUpperCase()}
        </span>
      );
    })}
  </div>
  )
}
