const mongoose= require('mongoose');
const {Schema, model}= mongoose;
const User= require('./User')

const PostSchema= new Schema({
    title: String,
    date: String,
    categories: Array,
    link: String,
    summary: String,
    content: String,
    image: String,
    author: {
        type: Schema.Types.ObjectId,
        ref: 'User',
      },
});

const Post= model('Post', PostSchema);

module.exports = Post;
