import React from "react";
import "bootstrap/dist/css/bootstrap.css";
import './css/masonry-post.css'
import categoryColors from './styles.js'

export default function MasonryPost({ post, tagsOnTop }) {
  const style = {
    backgroundImage: `url("${require(`../assets/images/${post.image}`)}")`,
  };
  return (
    <a className="masonry-post overlay" style={style} href={post.link}>
      <div className="image-text">
        <div className="tags-container">
               {post.categories.map((tag,index)=>{
                return <span key={index} className="tag" style={{backgroundColor: categoryColors[tag]}}>{tag.toUpperCase()}</span>
               })}
        </div>
        <div>
          <h2 className="image-title">{post.title}</h2>
          <span className="image-date">{post.date}</span>
        </div>
      </div>
    </a>
  );
}
