import React, { useEffect, useRef } from "react";
import "bootstrap/dist/css/bootstrap.css";
import "./css/user-post.css";
import { Navigate } from "react-router-dom";
import { useNavigate } from "react-router";


export default function UserPost({ post, isFirst }) {
  const navigate= useNavigate();
  const postTextRef = useRef(null);
  const postPageRender = () => {
    // navigate({{`/post/${post._id}`}});
    return <Navigate to={`/post/${post._id}`} />;
  };


  return (
    <div className="post-container">
      <div
        onClick={postPageRender}
        className={`post-image ${isFirst && "first-post"}`}
      >
        <img
          src={`${require(`../../rest/uploads/${post.image.slice(8)}`)}`}
          alt="Post Image"
        />
      </div>
      <div className={`post-text ${isFirst && "first-post"}`} ref={postTextRef}>
        <div>
          <h2 onClick={postPageRender}>{post.title}</h2>
          <span>{post.author.username}</span>
          <span>{post.date}</span>
        </div>
        <p>{post.summary}</p>
      </div>
    </div>
  );
}
