import React, { useState, useRef, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.css";
import "./css/home.css";
import PostMasonry from "../components/post-masonry";
// import trending from "../assets/mocks/trending";
// import featured from "../assets/mocks/featured";
import MasonryPost from "../components/masonry-post";
import PostGrid from "../components/post-grid";

const trendingConfig = {
  1: {
    gridArea: "1/2/3/3",
  },
};

const featuredConfig = {
  0: {
    gridArea: "1/1/2/3",
    height: "300px",
    width: "100%",
  },
  1: {
    height: "300px",
    width: "100%",
  },
  3: {
    height: "630px",
    marginLeft: "30px",
    width: "630px",
  },
};

const mergeStyles = function (posts, config) {
  posts.forEach((post, index) => {
    post.style = config[index];
    // post.author = "Manya Bansal";
    // post.description =
      // "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.";
  });
};





function Home() {
  const [current, setCurrent] = useState(1);
  const recentPostsRef = useRef(null);
  const [paginationChanged, setPaginationChanged] = useState(false);
  const [posts, setPosts] = useState([]);

useEffect(() => {
  const fetchData = async () => {
    try {
      const response = await fetch("http://localhost:8000/post");
      const posts = await response.json();
      setPosts(posts);
    } catch (error) {
      console.error("Error fetching posts:", error);
    }
  };

  fetchData();
}, []);

  const trending = posts.slice(0, Math.min(posts.length-1, 4));
  const featured = posts.slice(0, Math.min(posts.length-1, 3));
 
  mergeStyles(trending, trendingConfig);
  mergeStyles(featured, featuredConfig);
  console.log(trending);
const recentPosts = [...trending, ...featured, ...featured];
const lastPost = featured.pop();
  // console.log(lastPost);
  useEffect(() => {
    if (paginationChanged) {
      recentPostsRef?.current.scrollIntoView({ behavior: "smooth" });
      setPaginationChanged(false);
    }
  }, [paginationChanged]);

  return (
    <main className="home">
      <section className="container">
        <div className="row">
          <h1>Featured Posts</h1>
          <section className="featured-posts-container">
            {/* <PostMasonry posts={featured} columns={2} tagsOnTop={true} /> */}
            <MasonryPost
              post={lastPost}
              tagsOnTop={true}
              styles={{ marginTop: "40px" }}
            />
          </section>

          <section className="container">
            <div className="row" ref={recentPostsRef}>
              <h1>Recent Posts</h1>
              {/* <PostGrid
                posts={recentPosts}
                current={current}
                setCurrent={setCurrent}
                setPaginationChanged={setPaginationChanged}
              /> */}
            </div>
          </section>

          <h1>Trending Posts</h1>
          <PostMasonry posts={trending} columns={3} />
        </div>
      </section>
    </main>
  );
}

export default Home;
