import ReactQuill from "react-quill";
import "bootstrap/dist/css/bootstrap.css";
import 'react-quill/dist/quill.snow.css';
import "./css/create-post.css"
import { useState } from "react";
export default function CreatePost(){
    const [title, setTitle] =useState('');
    const [summary, setSummary] = useState('');
    const [content, setContent] = useState('');
    const modules = {
        toolbar: [
          [{ 'header': [1, 2, false] }],
          ['bold', 'italic', 'underline','strike', 'blockquote'],
          [{'list': 'ordered'}, {'list': 'bullet'}, {'indent': '-1'}, {'indent': '+1'}],
          ['link', 'image'],
          ['clean']
        ],
      };

      const  formats = [
        'header',
        'bold', 'italic', 'underline', 'strike', 'blockquote',
        'list', 'bullet', 'indent',
        'link', 'image'
      ];
    return(
        <form>
              <div class="form-group">
              <label className="top">Title</label>
             <input  type="text" class="form-control" placeholder={'Title'} />
             </div>
            {/* <input style={{'margin-top': '200px'}} type="title" placeholder={'Title'}/> */}
            <div className="form-group">
                <label>Summary</label>
            <input className="form-control" type="summary" placeholder={'Summary'}/>
            </div>
            <div className="form-group">
            <label>File</label>
            <input className="form-control"type='file' />
            </div>
            <div >
            <label>Content</label>
            <ReactQuill value={content} modules={modules} formats={formats}/>
            </div>
           
            <button type="submit" class="btn btn-dark">Create Post</button>
        </form>

    )
}
