import Recat, { useEffect } from 'react';
import './css/tag-row.css'

export default function TagRow({tags}){
  // const [tags, setTags]= useState([]);
  // useEffect(()=>{
  //  fetch(`http://localhost:8000/tags/${id}`).then(response =>{
  //   response.json().then(tagsInfo=>{
  //     setTags(tagsInfo);
  //   //  console.log(authorInfo.username)
  //   })
  // });
  // }, []);
 
  return (
    <div className="tags-container">
    {tags.map((tag, index) => {
      return (
        <span
          key={index}
          className="tag"
          style={{ backgroundColor: tag.color }}
        >
          {tag.name}
        </span>
      );
    })}
  </div>
  )
}
