import React , {useState} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import "./css/navigation.css";
import {Avatar} from 'antd';

const navLinks=[
    {
        title: "Home",
        path: '/home'
    },
    {
        title: 'Blog',
        path: '/blog'
    },
    {
        title: 'Contact Us',
        path: '/contact-us'
    }
];

function Navigation({user}){
    const [menuActive, setMenuActive] = useState(false);
    const [loggedIn, setLoggedIn] = useState(true);
    return(
    <div className='navigation-bar'>
    <div className="container">
   <nav className="navbar navbar-expand-lg navbar-light d-flex py-3">
    <a className="navbar-brand" href="/">MiniBlog</a>
    
    <div className={`navbar-collapse d-flex ${menuActive && 'active'}`}>
      <div className="nav navbar-nav" >
      {
        navLinks.map((link,index)=>{
           return <a key={index} className="nav-item nav-link" href={link.path}>{link.title}</a>
        })
      }
          <div className="d-lg-none">
                <a className={`nav-item nav-link ${loggedIn && "d-none"}`} href="/login">Login</a>
                <a className={`nav-item nav-link ${loggedIn && "d-none"}`} href="/sign-up">Sign Up</a>
                <a className={`nav-item nav-link ${!loggedIn && "d-none"}`} href="/">Log Out</a>
          </div>
      </div>
      
      <div className={`buttons navbar-nav d-lg-flex d-none d-lg-block align-items-center ${loggedIn && "d-none"}`} >
       <a className=" button nav-item nav-link me-2"> <button type="button" className="btn btn-outline-dark">Login</button> </a>
       <a className="button nav-item nav-link">  <button type="button" className="btn btn-dark">Sign Up</button> </a>
      </div>
      <span className={`avatar-span d-flex align-items-center nav-item ${loggedIn && "d-none"}`}>
       <Avatar className=" me-2" style={{ backgroundColor: '#fde3cf', color: '#f56a00' }}>M</Avatar>
       <span className='avatar-name'>{`${user.firstName} ${user.lastName}`}</span>
      </span>
      
    </div>
    <span className="navbar-toggler-icon d-lg-none" onClick={()=> setMenuActive(!menuActive)} />
</nav>
</div>
</div>
    )
}

export default Navigation;
