import React, {useEffect, useState} from 'react';
import { useParams } from 'react-router';
import './css/post-page.css'
// import "bootstrap/dist/css/bootstrap.css";

export default function PostPage(){
    const {id} = useParams();
    const [postInfo, setPostInfo] = useState(null);
    const [userInfo, setUserInfo] = useState(null);
    useEffect(()=>{
          
          fetch(`http://localhost:8000/user/${id}`).then(response =>{
            response.json().then(userInfo=>{
              setUserInfo(userInfo);
            })
          });

          fetch(`http://localhost:8000/post/${id}`)
          .then(response=>{
            response.json().then(postInfo=>{
              setPostInfo(postInfo);
            })
          })
    }, []);
    //  console.log(postInfo);
    // console.log(postInfo.image.slice(8))
    if(!postInfo) return '';
    return (
        <div className='post-page'>
            <div className='post-image'>
            <img src= {`${require(`../../rest/uploads/${postInfo.image.slice(8)}`)}`}/>
            </div>
           <h1>{postInfo.title}</h1>
           <p className="author-text">
           <span className='post-date'>{postInfo.date}</span>
                <span className='post-author'>
                  By: @
                  <a href={`/posts/${postInfo.author._id}`}>{postInfo.author.username}</a>
                </span>
                
              </p>
           <div className=" post-content" dangerouslySetInnerHTML={{__html:postInfo.content}}/>
        </div>
    );
}
