import React, {useMemo } from "react";
import { Pagination } from "antd";
import TagRow from "./tag-row";
// import "bootstrap/dist/css/bootstrap.css";
import './css/post-grid.css'


export default function PostGrid({posts, current, setCurrent,setPaginationChanged}) {

  const onChange = (page) => {
    setCurrent(page);
    setPaginationChanged(true);
  };
  
  const pageSize= 9;
  const paginatedPosts = useMemo(() => {
    const lastIndex = current * pageSize;
    const firstIndex = lastIndex - pageSize;

    return posts.slice(firstIndex, lastIndex);
  }, [current]);
  
  

  return (
    <section className="pagination-container">
      <section className="post-grid container">
        {paginatedPosts.map((post, index) => {
         return   <div key={index} className="post-container">
            <figure>
              <a href={post.link}>
                <img 
                  src={require(`../assets/images/${post.image}`)}
                  alt={post.image}
                />
              </a>
            </figure>
            <TagRow tags={post.categories} />
            <h2>{post.title}</h2>
            <p className="author-text">
              <span>
                By:
                <a href={`/authors/${post.author}`}>{post.author}</a>
              </span>
              <span>- {post.date}</span>
            </p>
            <p className="description-text">{post.description}</p>
            <a href={post.link}>Read More...</a>
          </div>;
        })}
      </section>
      <Pagination
        className="paginationItem"
        defaultCurrent={current}
        onChange={onChange}
        total={posts.length}
      />
    </section>
  );
}
