import React, { useState, useRef, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.css";
import "./css/home.css";
import PostMasonry from "../components/post-masonry";
// import trending from "../assets/mocks/trending";
// import featured from "../assets/mocks/featured";
import MasonryPost from "../components/masonry-post";
import PostGrid from "../components/post-grid";

const trendingConfig = {
  1: {
    gridArea: "1/2/3/3",
  },
};

const featuredConfig = {
  0: {
    gridArea: "1/1/2/3",
    height: "300px",
    width: "100%",
  },
  1: {
    height: "300px",
    width: "100%",
  },
  3: {
    height: "630px",
    marginLeft: "30px",
    width: "630px",
  },
};

const mergeStyles = function (posts, config) {
  posts.forEach((post, index) => {
    post.style = config[index];
  });
};

function Home() {
  const [current, setCurrent] = useState(1);
  const recentPostsRef = useRef(null);
  const [paginationChanged, setPaginationChanged] = useState(false);
  const [posts, setPosts] = useState({});
  const [trending, setTrending] = useState([]);
  const [featured, setFeatured] = useState([]);
  const [initialRender, setInitialRender] = useState(true);

  useEffect(() => {
    fetch("http://127.0.0.1:8000/post")
      .then(async (response) => {
        await response.json().then((posts) => {
          setPosts(posts);
          setTrending(posts.trending);
          setFeatured(posts.featured);
        });
      })
      .catch((err) => console.log(err));
  }, []);

  mergeStyles(trending, trendingConfig);
  mergeStyles(featured, featuredConfig);

  const recentPosts = [...trending, ...featured, ...featured];
  const lastPost = featured[featured.length - 1];

  useEffect(() => {
    if (paginationChanged) {
      recentPostsRef?.current.scrollIntoView({ behavior: "smooth" });
      setPaginationChanged(false);
    }
  }, [paginationChanged]);

  useEffect(() => {
    if (initialRender) {
      setInitialRender(false);
    }
  }, []);

  if (initialRender || (trending.length > 0 && featured.length > 0)) {
    console.log(lastPost);
    console.log(featured);
    console.log(trending);

    return (
      <main className="home">
        {/* ... rest of the component code */}
      </main>
    );
  }

  return null; // or loading state JSX
}

export default Home;
