import React, { useContext, useEffect, useState } from "react";
import { useParams } from "react-router";
import {Link} from "react-router-dom"
import { UserContext } from "../user-context";
import "bootstrap/dist/css/bootstrap.css";
import "./css/post-page.css";
import TagRow from "../components/tag-row";


export default function PostPage() {
  const { id } = useParams();
  const [postInfo, setPostInfo] = useState(null);
  const {userInfo}= useContext(UserContext);
  const [isLoading, setIsLoading] = useState(true); // Add a loading state

  useEffect(() => {
    fetch(`http://127.0.0.1:8000/post/${id}`).then(async (response) => {
      await response.json().then((postInfo) => {
        setPostInfo(postInfo);
        setIsLoading(false); // Set loading state to false when data is fetched
      });
    });
  }, []);

  if (isLoading) {
    return <div>Loading...</div>;
  }
  
  if (!postInfo) return "";
  return (
    <div className="post-page">
      <div className="post-page-image">
        <img
          src={`${require(`../../rest/uploads/${postInfo.image.slice(8)}`)}`}
        />
      </div>
      <h1>{postInfo.title}</h1>
      <p className="author-text">
        <span className="post-date">{postInfo.date}</span>
        <span className="post-author">
          By: @
          <a href={`/posts/${postInfo.author._id}`}>
            {postInfo.author.username}
          </a>
        </span>
      </p>
      <span className="tags">
        <TagRow tags={postInfo.categories} />
      </span>

      <div
        className=" post-content"
        dangerouslySetInnerHTML={{ __html: postInfo.content }}
      />
      <div className={`${userInfo.id!==postInfo.author._id && "d-none"}`}>
       <Link to={`/edit/${id}`}> <button >Edit</button></Link>
        <button>Delete</button>
      </div>
    </div>
  );
}
