import React, { useState, useRef, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.css";
import "./css/home.css";
import PostMasonry from "../components/post-masonry";
// import trending from "../assets/mocks/trending";
// import featured from "../assets/mocks/featured";
import MasonryPost from "../components/masonry-post";
import PostGrid from "../components/post-grid";

const trendingConfig = {
  1: {
    gridArea: "1/2/3/3",
  },
};

const featuredConfig = {
  0: {
    gridArea: "1/1/2/3",
    height: "300px",
    width: "100%",
  },
  1: {
    height: "300px",
    width: "100%",
  },
  3: {
    height: "630px",
    marginLeft: "30px",
    width: "630px",
  },
};

const mergeStyles = function (posts, config) {
  posts.forEach((post, index) => {
    post.style = config[index];
  });
};

function Home() {
  const [current, setCurrent] = useState(1);
  const recentPostsRef = useRef(null);
  const [paginationChanged, setPaginationChanged] = useState(false);
  const [posts, setPosts] = useState({});

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch("http://127.0.0.1:8000/post");
        const { trending, featured } = await response.json();
        setPosts({ trending, featured });
      } catch (error) {
        console.log(error);
      }
    };
  
    fetchPosts();
  }, []);
// console.log(posts.length);
console.log(posts);
  const trending =  posts.trending;
  const featured = posts.featured;
//  console.log(trending);
  // mergeStyles(trending, trendingConfig);
  // mergeStyles(featured, featuredConfig);

const recentPosts = [...trending, ...featured, ...featured];
const lastPost = featured.pop();
  // console.log(featured);
 
  useEffect(() => {
    if (paginationChanged) {
      recentPostsRef?.current.scrollIntoView({ behavior: "smooth" });
      setPaginationChanged(false);
    }
  }, [paginationChanged]);
  if(posts.length>0){
   return (
    <main className="home">
      <section className="container">
        <div className="row">
          <h1>Featured Posts</h1>
          <section className="featured-posts-container">
            <PostMasonry posts={featured} columns={2} tagsOnTop={true} />
            <MasonryPost
              post={lastPost}
              tagsOnTop={true}
              styles={{ marginTop: "40px" }}
            />
          </section>

          <section className="container">
            <div className="row" ref={recentPostsRef}>
              <h1>Recent Posts</h1>
              <PostGrid
                posts={recentPosts}
                current={current}
                setCurrent={setCurrent}
                setPaginationChanged={setPaginationChanged}
              />
            </div>
          </section>
          <h1>Trending Posts</h1>
          <PostMasonry posts={trending} columns={3} />
        </div>
      </section>
    </main>
  );
  }
  
}

export default Home;
