import React, { useMemo } from "react";
import { Pagination } from "antd";
import TagRow from "./tag-row";
import { Link } from "react-router-dom";
import "./css/post-grid.css";

export default function PostGrid({
  posts,
  current,
  setCurrent,
  setPaginationChanged,
}) {
  const onChange = (page) => {
    setCurrent(page);
    setPaginationChanged(true);
  };

  const pageSize = 9;
  const totalPosts = posts.length;
  const totalPages = Math.ceil(totalPosts / pageSize);

  const paginatedPosts = useMemo(() => {
    const firstIndex = (current - 1) * pageSize;
    const lastIndex = current * pageSize;

    return posts.slice(firstIndex, lastIndex);
  }, [posts, current]);

  return (
    <section className="pagination-container">
      <section className="post-grid container">
        {paginatedPosts.map((post, index) => (
          <div key={index} className="post-container">
            <Link to={`/post/${post._id}`}>
              <figure className="post-image">
                <img
                  src={require(`../../rest/uploads/${post.image.slice(8)}`)}
                  alt={post.image}
                />
              </figure>
            </Link>
            <TagRow tags={post.categories} />
            <h2>{post.title}</h2>
            <p className="author-text">
              <span>
                By: <Link to={`/posts/${post.author._id}`}>{post.author.username}</Link>
              </span>
              <span className="post-date">- {post.date}</span>
            </p>
          </div>
        ))}
      </section>
      <Pagination
        className="paginationItem"
        current={current}
        onChange={onChange}
        total={totalPosts}
        pageSize={pageSize}
        showSizeChanger={false}
        showTotal={(total) => `Total ${total} posts`}
        pageSizeOptions={[pageSize.toString()]}
        defaultPageSize={pageSize}
        defaultCurrent={1}
        hideOnSinglePage={true}
        showLessItems={true}
        responsive={true}
        showQuickJumper={false}
        showTitle={false}
        showEllipsis={true}
        total={totalPosts}
      />
    </section>
  );
}
